echo "./plot --alpha-out --alpha-side --alpha-long"
outputs[0]=$(./plot --alpha-out --y-max 3.1 --y-min -0.2 --leg-pos .65,0.8,.65,.9 -f $files 2>&1)
outputs[1]=$(./plot --alpha-side --y-max 3.1 --y-min -0.2 --leg-pos .65,0.8,.2,.45 -f $files 2>&1)
outputs[2]=$(./plot --alpha-long --y-max 2 --y-min -0.2 --leg-pos .65,0.8,.2,.45 -f $files 2>&1)

echo "./plot --r-out --r-side --r-long"
outputs[3]=$(./plot --r-out --y-max 2.7 --y-min 0 --leg-pos .65,0.8,.2,.45 -f $files 2>&1)
# make output specific if files have "ohp" in them
if [[ $files == *"ohp"* ]]; then
  outputs[3]=$(./plot --r-out --y-max 1.5 --y-min 0 --leg-pos .65,0.8,.2,.45 -f $files 2>&1)
fi 
outputs[4]=$(./plot --r-side --y-max 2.0 --y-min 0 --leg-pos .65,0.8,.2,.45 -f $files 2>&1)
outputs[5]=$(./plot --r-long --y-max 2.7 --y-min 0 --leg-pos .65,0.8,.2,.45 -f $files 2>&1)
