echo "./plot --lambda --c0 --eps separately"
outputs[6]=$(./plot --lambda --y-max 1.2 --y-min -0.3 --leg-pos .65,0.8,.25,.5 -f $files 2>&1)
outputs[7]=$(./plot --c0 --eps  --y-max 1.2 --y-min -0.3 --leg-pos .65,0.8,.3,.75 -f $files 2>&1)
outputs[8]=$(./plot --chi2  --y-max 10.0 --y-min -0.3 --leg-pos .65,0.8,.3,.75 -f $files 2>&1)

# Loop over the array
for output in "${outputs[@]}"; do
  # Extract the filename from the output
  filename=$(echo "$output" | grep -o 'pdf file .* has been created' | sed 's/^pdf file //;s/ has been created$//')
  echo "$output"
  filenames+=("$filename")

  # # Open the file
  # xdg-open "$filename"
done
# file name should be merge_ and continue with the end of the filename after "c2" string
merged_filename="merge_$(echo "${filenames[0]}" | grep -o 'c2.*')"
pdfunite "${filenames[@]}" "$merged_filename"

# Open the merged file
xdg-open "$merged_filename"